// Helper function to describe order status
function getStatusDescription(status) {
  const statusMap = {
    '10': 'Initialization',
    '20': 'Payment in progress', 
    '30': 'Success',
    '40': 'Failure',
    '50': 'Order closed'
  };
  return statusMap[status] || 'Unknown status';
}

